package Dec18;

public class ForLoopFibonacci {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-1,b=1,c;
		for(int i=1;i<=10;i++)
		{
			c=a+b;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
			System.out.println(c);
			a=b;
			b=c;
			
		}

	}

}
