package Dec22;

public class Vowelconsonant {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='a';
		if(ch=='a'||ch=='e'||ch=='i'||ch=='o'||ch=='u')
		{
			System.out.println(ch+" is a Vowel");
		}
		else
		{
			System.out.println(ch+" is a Consonant");
		}
	}

}
